import Multiselect from 'vue-multiselect'
import {axios} from '../../helpers/axios';
import { required, requiredIf } from 'vuelidate/lib/validators'
var API_IP = process.env.VUE_APP_API_IP

export default {
  props: ['hide_modal','id_pay'],
  components: {
    Multiselect
  },
  data () {
    return {
      _id:"",
      estado:"",
      numero:"",
      fecha:"",
      valor:"",
      solicitud:"",
      planillas:[],
      planilla_fecha:"",
      planilla_numero:"",
      numero_ORPA:"",
      auditoria:"",
      hide: this.hide_modal
    }
  },
  mounted () {
    axios
    .get(API_IP+'/payment/',this.id_pay)
    .then(response => {
      console.log(response);
      this.numero = response.data.data[0].numero
      this._id = response.data.data[0]._id
      this.valor = response.data.data[0].valor
      this.fecha = response.data.data[0].fecha.substring(0,10)
      this.planillas = response.data.data[0].planillas
      this.planilla_fecha= response.data.data[0].planillas[0].planilla_fecha.substring(0,10)
      this.planilla_numero=response.data.data[0].planillas[0].planilla_numero.substring(0,10)
      this.numero_ORPA = response.data.data[0].numero_ORPA
      this.solicitud = response.data.data[0].solicitud
      this.auditoria = response.data.data[0].auditoria
      this.estado = response.data.data[0].estado

      }
    );
  },
  methods : {
    clearTypes(){
      this.student_program= "",
      this.teacher_type= "",
      this.teacher_ocupation= "",
      this.plant_appoiment="",
      this.ods_number=""
    },
    submit() {
      this.$v.$touch()
      if (this.$v.$invalid) {
        this.$swal({
          title: 'Error!',
          text: 'Debe llenar campos necesarios en el formulario',
          type: 'error',
          confirmButtonText: 'Cerrar'
        });
      } else {
        this.submitting = true;
        var personData= {}
        personData['dependencia']= this.dependence
        personData['numero_documento']= this.document_number
        personData['tipo_documento']= this.document_type
        personData['primer_nombre']= this.name_1
        personData['segundo_nombre']= this.name_2
        personData['primer_apellido']= this.lastname_1
        personData['segundo_apellido']= this.lastname_2
        personData['correo_electronico']= this.email
        personData['ciudad']= this.city
        personData['direccion']= this.address
        personData['telefono']= this.phone
        personData['celular']= this.mobil_phone
        personData['extension']= this.extension
        personData['celular']= this.mobil_phone

        if(this.person_type == 'Profesor'){
          personData['profesor']={
            tipo: this.teacher_type,
            dedicacion: this.teacher_ocupation
          }
        }else if (this.person_type == 'Estudiante') {
          personData['estudiante']={
            programa: this.student_program._id
          }
        }else if (this.person_type == 'Contratista') {
          if (this.ods_number=="") {
            this.ods_number="-"
          }
          personData['contratista']={
            ODS: this.ods_number
          }
        }else if (this.person_type == 'Planta') {
          personData['planta']={
            cargo:this.plant_appoiment
          }
        }
        axios
        .post(API_IP+"/person", personData )
        .then(response => {
          console.log(response);
          this.$swal({
            title: 'Exito!',
            text: 'Se creo la persona satisfactoriamente.',
            type: 'success',
            confirmButtonText: 'Aceptar'
          }).then((result) => {
            if (result.value) {
              this.submitting = false;
              this.hide = true;
              this.$emit('modalShow',this.hide)
            }
          });
        })
        .catch(error => {
          console.log(error);
          this.$swal({
            title: 'Error!',
            text: error.response.data.error.msg,
            type: 'error',
            confirmButtonText: 'Cerrar'
          });
          this.submitting = false;
        });
      }
    },
    close() {
      this.$swal({
        title: 'Espere',
        text: '¿Esta seguro que quiere cancelar la operación?',
        type: 'warning',
        showCancelButton: true,
        confirmButtonText: 'Si',
        cancelButtonText: 'No',
      }).then((result) => {
        if (result.value) {
          this.$swal({
            title: 'Cancelado',
            type: 'success'
          }).then((result) => {
            if (result.value) {
              this.hide = true;
              this.$emit('modalShow',this.hide);
            }
          });
        }
      });
    }
  }
}
